SELECT DISTINCT E.EMPLOYEE_ID, E.FK_DEPARTMENT_ID, E.NAME, E.GRADELEVEL, E.FK_TEAM_ID ,CASE E.GRADELEVEL WHEN 1 THEN '사원' WHEN 3 THEN '팀장' WHEN 5 THEN '부서장' ELSE '사장' END AS GRADE FROM TBL_EMPLOYEES U JOIN TBL_EMPLOYEES E ON U.FK_DEPARTMENT_ID = E.FK_DEPARTMENT_ID
WHERE U.EMPLOYEE_ID != #{loginuser.employee_id} AND U.FK_DEPARTMENT_ID = #{loginuser.fk_department_id} AND E.GRADELEVEL > #{loginuser.gradelevel}
ORDER BY E.GRADELEVEL DESC

SELECT DISTINCT DEPARTMENT_ID, DEPARTMENT_NAME FROM TBL_DEPARTMENT D JOIN TBL_EMPLOYEES E ON D.DEPARTMENT_ID = E.FK_DEPARTMENT_ID
WHERE D.DEPARTMENT_ID = #{loginuser.fk_department_id}
ORDER BY D.DEPARTMENT_ID

SELECT DISTINCT DEPARTMENT_ID, DEPARTMENT_NAME, NVL(TEAM_ID, '0') AS TEAM_ID, TEAM_NAME FROM TBL_TEAM T RIGHT JOIN TBL_DEPARTMENT D ON D.DEPARTMENT_ID = T.FK_DEPARTMENT_ID JOIN TBL_EMPLOYEES E ON E.FK_DEPARTMENT_ID = D.DEPARTMENT_ID
WHERE D.DEPARTMENT_ID = #{loginuser.fk_department_id}
ORDER BY TEAM_ID

