mapper.update:
	    update tbl_freeboard set subject = #{subject}
	                           , content = #{content}

	    where seq = #{seq}

	    update tbl_freeboard set subject = #{subject}
	                           , content = #{content}
	                           , filename = #{fileName}
	                           , orgFilename = #{orgFilename}
   							   , fileSize = #{fileSize}
	    where seq = #{seq}

mapper.select:
select ceil(count(*)/#{sizePerPage})
from tbl_free_comment
where parentseq = #{parentSeq}

  
	    select fileName, orgFilename
	    from tbl_free_comment
	    where seq = #{seq}

  
	    select content
	    from tbl_free_comment
	    where seq = #{seq}

  
	    select fileName, orgFilename, parentSeq
	    from tbl_free_comment
	    where seq = #{seq}

mapper.update:
update tbl_noticeboard set subject = #{subject}
                         , content = #{content}
where seq = #{seq}

update tbl_noticeboard set subject = #{subject}
                         , content = #{content}
                         , file_name = #{file_Name}
                         , org_Filename = #{org_Filename}
                         , file_Size = #{file_Size}
where seq = #{seq}
